import React, { useState } from "react";

function Filter() {
  let pupil = [
    { name: "macmuka" },
    { name: "eric" },
    { name: "josh" },
    { name: "tonny" },
  ];
  let [students, setStudents] = useState(pupil);
  function itemClicked(e) {
    let m = e.target.getAttribute("removeKid");
    // students.filter((ckid) => {
    //   return !ckid.m;
    // });
    setStudents(
      pupil.filter((ckid) => {
        return !ckid.m;
      })
    );
  }
  return (
    <div>
      {students.map((kid) => {
        return (
          <div>
            <p>
              {kid.name}&nbsp;&nbsp;
              <button removeKid={kid.name} onClick={itemClicked}>
                X
              </button>
            </p>
          </div>
        );
      })}
    </div>
  );
}

export default Filter;
